CREATE VIEW dbo.web40repMaclist AS Select Top 1000000 tblAssets.AssetID As ID, tblAssets.AssetUnique, tblAssets.Domain, tsysOS.OSname, tblNetwork.Description As [Network Card], tblNetwork.MACaddress, tsysOS.Image As icon, tblNetwork.IPAddress, tblNetwork.Lastchanged, tblAssetCustom.Custom1, tblAssetCustom.Custom2, tblAssetCustom.Custom3, tblAssetCustom.Custom4, tblAssetCustom.Custom5, tblAssetCustom.Custom6, tblAssetCustom.Custom7, tblAssetCustom.Custom8, tblAssetCustom.Custom9, tblAssetCustom.Custom10, tblAssetCustom.Custom11 From tblAssets Inner Join tblNetwork On tblAssets.AssetID = tblNetwork.AssetID Inner Join tblAssetCustom On tblAssets.AssetID = tblAssetCustom.AssetID Inner Join tsysOS On tblAssets.OScode = tsysOS.OScode Where tblNetwork.IPEnabled = 1 And tblAssetCustom.State = 1 Order By tblAssets.AssetName