CREATE VIEW dbo.web40repmemoryless2GB AS Select Top 1000000 tblAssetCustom.Custom1 As Организация, tblAssets.AssetID, tblAssets.AssetUnique, tblAssets.Domain, tblAssets.Description, tsysOS.OSname, tblAssets.Memory, tblAssetCustom.Model, tblAssets.Lastseen, tsysOS.Image As icon From tblAssets Inner Join tblAssetCustom On tblAssets.AssetID = tblAssetCustom.AssetID Inner Join tsysOS On tblAssets.OScode = tsysOS.OScode Where tblAssets.Memory < 2048 And tblAssetCustom.State = 1 Order By tblAssets.AssetName, Организация