
CREATE VIEW [dbo].[web40repsoftwarePC] AS Select Top 1000000 tblAssets.AssetID, tblAssets.AssetUnique, tblAssets.Domain, tblSoftwareUni.softwareName As software, tblSoftware.softwareVersion As version, tblSoftwareUni.SoftwarePublisher As publisher, tsysOS.Image As icon From tblSoftware Inner Join tblAssets On tblSoftware.AssetID = tblAssets.AssetID Inner Join tblSoftwareUni On tblSoftware.softID = tblSoftwareUni.SoftID Inner Join tblAssetCustom On tblAssets.AssetID = tblAssetCustom.AssetID Inner Join tsysOS On tblAssets.OScode = tsysOS.OScode Where tblAssetCustom.State = 1 Order By tblAssets.AssetName, tblSoftwareUni.softwareName, tblSoftware.softwareVersion