CREATE VIEW dbo.web50rep39bb42eede464fb1b6649f9ee4b8915e AS Select Top 1000000 tblAssets.AssetID, tblAssets.AssetUnique, tblAssets.Domain, tsysOS.OSname, tblAssets.Description, tsysOS.Image As icon, tblAssets.Firstseen, tblAssetCustom.Custom1 As Организация, tblAssetCustom.Custom2 As Город, tblAssetCustom.Custom3 As Адрес, tblAssetCustom.Custom4 As Офис, tblAssetCustom.Custom5 As Место, tblAssetCustom.Custom6 As [Балансодержатель оборудования], tblAssetCustom.Custom7 As [Номенклатурный номер], tblAssetCustom.Custom8 As [Инвентариный номер], tblAssetCustom.Custom9 From tblAssets Inner Join tblAssetCustom On tblAssets.AssetID = tblAssetCustom.AssetID Inner Join tsysOS On tblAssets.OScode = tsysOS.OScode Where tblAssets.Firstseen > GetDate() - 7 And tblAssetCustom.State = 1 Order By tblAssets.Firstseen Desc, tblAssets.AssetName