CREATE VIEW dbo.web50rep53476cdabb4041f1b2c61a4a05bbfae5 AS Select Top 1000000 ac.Custom1 As 'Организация', ac.Custom10 As 'Должность пользователя', ac.Custom11 As 'ФИО пользователя', ac.Custom19 As 'ФИО ответственного ВТшника', a.AssetName, o.OSname, slo.Comments As 'Владелец лицензий', l.LicenseOwner As 'Производитель ПО', sn.Product As N'Название ПО', lt.LicenseTypeName As 'Тип лицензии', slo.Nrlicenses As 'Кол-во лицензий', l.LicenseTypeComments As 'Комментарий к типу лицензий', sn.ProductKey As 'Сканированный SN', slo.Licensekey As 'SN по договору', sn.ProductID From tblAssets As a Inner Join tblSerialnumber As sn On a.AssetID = sn.AssetID Inner Join tblAssetCustom As ac On a.AssetID = ac.AssetID Inner Join tsysOS As o On a.OScode = o.OScode Left Join tblSublicensesOrders As slo On sn.ProductKey = Cast(slo.Licensekey As nvarchar) Left Join tblLicenses As l On slo.LicenseidID = l.LicenseidID Left Join tsyslicensetype As lt On l.LicenseType = lt.LicenseType Where ac.State = 1 Order By N'Название ПО', a.AssetName