CREATE VIEW dbo.web50rep55b70c06c2a642f0ada0254776eff9e0 AS Select Top 1000000 tsysIPLocations.IPLocation, tsysIPLocations.Realstart, tsysIPLocations.Realend, tblAssets.IPAddress As [IP устройства], tblAssets.AssetName As [Имя усройства] From tblAssets Inner Join tblAssetCustom On tblAssets.AssetID = tblAssetCustom.AssetID Inner Join tsysAssetTypes On tsysAssetTypes.AssetType = tblAssets.Assettype Inner Join tsysIPLocations On tsysIPLocations.LocationID = tblAssets.LocationID Where tblAssets.IPAddress != '' And tblAssets.IPAddress Is Not Null Order By tsysIPLocations.IPLocation