CREATE VIEW dbo.web50rep60bda52700d34dabb9e45791331ec2b5 AS Select Top 1000000 tblAssets.AssetName, tblAssets.AssetID, tblAssets.Domain, 'Not scanned' As OS, tblErrors.CFGname, tsysasseterrortypes.ErrorMsg As Type, tblErrors.ErrorText As Message, tblErrors.Lastchanged, 'notscanned.png' As icon From tblAssets Inner Join tblErrors On tblAssets.AssetID = tblErrors.AssetID Inner Join tblAssetCustom On tblAssets.AssetID = tblAssetCustom.AssetID Inner Join tsysasseterrortypes On tblErrors.ErrorType = tsysasseterrortypes.Errortype Where tblAssetCustom.State = 1 And Coalesce(tblAssets.OScode, '') = '' Order By tblErrors.Lastchanged Desc, tblAssets.AssetName