CREATE VIEW dbo.web50rep6f551c1bcc2c4149806f26d5bfab15a5 AS Select Top 1000000 tblAssets.AssetID As Id, t.AssetTypename, tblAssets.AssetName, ac.Custom19 As [Ответственный ВТшник], l.IPLocation From tblAssets Inner Join tblAssetCustom As ac On tblAssets.AssetID = ac.AssetID Inner Join tsysAssetTypes As t On t.AssetType = tblAssets.Assettype Left Join tsysIPLocations As l On l.StartIP <= tblAssets.IPNumeric And l.EndIP >= tblAssets.IPNumeric Where (t.AssetTypename = 'Windows' Or t.AssetTypename = 'Printer') And (ac.Custom19 Is Null Or ac.Custom19 = '') Order By l.IPLocation, tblAssets.AssetName, t.AssetTypename