CREATE VIEW dbo.web50rep7117ce366e4e483599d995af890737c2 AS Select Top 1000000 r.AssetId As Asset, ac.Custom1 As N'Организация', ac.Custom2 As N'Город', ac.Custom3 As N'Адрес', ac.Custom4 As N'Офис', s.ScladName As N'Склад', ac.Custom19 As N'ФИО ответственного ВТшника', r.*, ts.Statename As N'Статус' From rMaterialSvod As r Inner Join tblAssetCustom As ac On r.AssetId = ac.AssetID Inner Join tblState As ts On ac.State = ts.State Inner Join tblAssets As a On ac.AssetID = a.AssetID Left Join rAssetsScladLink As asl On r.AssetId = asl.AssetID Left Join rSclad As s On asl.IdSclad = s.Id Where ts.Statename Like '%ctive' Order By N'Город', N'Организация', r.IdSclad, Asset