CREATE VIEW dbo.web50rep73375ad7e282404587cf32968037d273 AS Select Top 1000000 tblAssets.AssetID, tblAssets.AssetName, ac.Custom1 As N'Филиал', ac.Custom11 As N'ФИО пользователя', tblUsersInGroup.Username, tblUsersInGroup.Domainname, tblUsersInGroup.Groupname From tblAssets Inner Join tblAssetCustom As ac On tblAssets.AssetID = ac.AssetID Inner Join tblUsersInGroup On tblAssets.AssetID = tblUsersInGroup.AssetID Where (tblUsersInGroup.Groupname Like 'admin%') Or (tblUsersInGroup.Groupname Like N'%дминистр%') Order By N'Филиал', tblAssets.AssetName