CREATE VIEW dbo.web50rep793ec2895f624d71b6d14202a3c60b9b AS Select Top 1000000 tblAssets.AssetID, tblAssets.AssetName, tsysAssetTypes.AssetTypename, tsysAssetTypes.AssetTypeIcon10 As icon, tblAssets.IPAddress, tblAssets.Lastseen, tblAssets.Lasttried, tblOperatingsystem.OSID, tblOperatingsystem.AssetID As AssetID1, tblOperatingsystem.BuildType, tblOperatingsystem.Caption, tblOperatingsystem.Codeset, tblOperatingsystem.OSType, tblOperatingsystem.OSProductSuite, tblOperatingsystem.Version From tblAssets Inner Join tblAssetCustom On tblAssets.AssetID = tblAssetCustom.AssetID Inner Join tsysAssetTypes On tsysAssetTypes.AssetType = tblAssets.Assettype Inner Join tblOperatingsystem On tblAssets.AssetID = tblOperatingsystem.AssetID Where tblAssetCustom.State = 1