CREATE VIEW dbo.web50rep8b2a13e28efd4622b9e2ae211b927b73 AS Select Top 1000000 ac.Custom1 As 'Организация', ac.Custom2 As 'Город', ac.Custom3 As 'Адрес', ac.Custom4 As 'Офис', ac.Custom19 As 'Ответственный ВТшник', p.Departament As Департамент, p.Upravlenie As Управление, p.Otdel As Отдел, p.Grupa As Группа, p.Dolgnost As Должность, p.FIO As ФИО, s.Statename, at.AssetTypename, a.AssetName, a.Processor, a.Memory From tblAssetCustom As ac Inner Join tblAssets As a On ac.AssetID = a.AssetID Inner Join tsysAssetTypes As at On a.Assettype = at.AssetType Inner Join tblState As s On ac.State = s.State Left Join rPersonal As p On p.FIO = ac.Custom11 Where ac.Custom1 = N'УСЦ' Order By p.Company, Департамент, Управление, Отдел, Группа, ФИО, at.AssetTypename Desc