CREATE VIEW dbo.web50rep8d340400f00d4cd69541653b716b870c AS Select Top 1000000 tblAssets.AssetID As ID, tsysIPLocations.IPLocation, tsysAssetTypes.AssetTypename, tblAssets.AssetName, tblAssets.IPAddress, tblAssetCustom.Model, tblAssetCustom.Manufacturer, tblAssetCustom.Serialnumber, tblAssetCustom.Custom1, tblAssetCustom.Custom2, tblAssetCustom.Custom3, tblAssetCustom.Custom4, tblAssetCustom.Custom5, tblAssetCustom.Custom6, tblAssetRelations.ParentAssetID, tblAssetRelations.ChildAssetID From tblAssets Left Join tsysIPLocations On tsysIPLocations.StartIP <= tblAssets.IPNumeric And tsysIPLocations.EndIP >= tblAssets.IPNumeric Inner Join tblAssetCustom On tblAssets.AssetID = tblAssetCustom.AssetID Inner Join tsysAssetTypes On tsysAssetTypes.AssetType = tblAssets.Assettype Full Join tblAssetRelations On tblAssets.AssetID = tblAssetRelations.ParentAssetID