

CREATE VIEW [web50repHarddriveSerialnumbers] AS SELECT Top 1000000 tsysAssetTypes.AssetTypeIcon16 AS icon, tblAssets.AssetID, tblAssets.AssetName, tblFloppy.SerialNumber, tblFloppy.Model as [HD Model], tblFloppy.Manufacturer as [HD Manufacturer], tblFloppy.Size, tblFloppy.FirmwareRevision, tblAssets.Domain, tblAssets.Username, tblAssets.Userdomain, tblAssets.IPAddress, tblAssets.Description, tblAssetCustom.Manufacturer, tblAssetCustom.Model, tblAssetCustom.Location, tsysIPLocations.IPLocation, tblAssets.Firstseen, tblAssets.Lastseen FROM tblFloppy INNER JOIN tblAssets ON tblFloppy.AssetID = tblAssets.AssetID INNER JOIN tsysAssetTypes ON tblAssets.Assettype = tsysAssetTypes.AssetType Inner Join tblAssetCustom On tblAssets.AssetID = tblAssetCustom.AssetID Inner Join tsysOS On tsysOS.OScode = tblAssets.OScode Left Join tsysIPLocations ON tblAssets.LocationID = tsysIPLocations.LocationID WHERE (tblFloppy.SerialNumber IS NOT NULL) ORDER BY tblAssets.AssetName