

CREATE VIEW [web50repSSHerrors] AS SELECT TOP 1000000 tblAssets.AssetName, tblAssets.AssetID, tsysasseterrortypes.ErrorMsg AS [Error Type], tblErrors.ErrorText AS Message, tblErrors.Lastchanged, 'notscanned.png' AS icon, tblAssets.Domain, tsysAssetTypes.AssetTypename As Type, tblAssets.IPAddress, tblAssets.Description, tblAssetCustom.Manufacturer, tblAssetCustom.Model, tblAssetCustom.Location, tsysIPLocations.IPLocation, tblAssets.Firstseen, tblAssets.Lastseen FROM tblAssets INNER JOIN tblErrors ON tblAssets.AssetID = tblErrors.AssetID INNER JOIN tblAssetCustom ON tblAssets.AssetID = tblAssetCustom.AssetID INNER JOIN tsysasseterrortypes ON tblErrors.ErrorType = tsysasseterrortypes.Errortype Left Join tsysIPLocations ON tblAssets.LocationID = tsysIPLocations.LocationID Inner Join tsysAssetTypes On tsysAssetTypes.AssetType = tblAssets.Assettype WHERE (tblAssetCustom.State = 1) AND (tsysasseterrortypes.Errortype = 6) ORDER BY tblErrors.Lastchanged DESC, tblAssets.AssetName