CREATE VIEW dbo.web50repb96f7e06dfa14e9e8989b36dc1e37c21 AS SELECT TOP 1000000  p.AssetId As 'Asset Id', a.AssetID, a.AssetName, ac.Custom1 As N'Организация', ac.Custom2 As N'Город', ac.Custom3 As N'Адрес', ac.Custom4 As N'Офис', ac.Custom19 As N'Ответственный ВТшник', ac.Model As N'Название модели', p.printedpages As N'Счетчик печати', p.datecheck As N'Дата' From rPrintHist As p Inner Join tblAssetCustom As ac On p.AssetId = ac.AssetID Inner Join tblAssets As a On p.AssetId = a.AssetID Inner Join tsysAssetTypes As at On a.Assettype = at.AssetType Left Join rModelLink As ml On ac.Model = ml.ModelAsset Left Join rModelDevice As md On ml.ModelSprav = md.Model Order By N'Организация', N'Город', ac.AssetID, N'Дата'