CREATE VIEW dbo.web50repbf7801a483d54e54b511bf9d5234dbcf AS Select Top 1000000 tblLoginLog.Date As LoginDate, Case tblLoginLog.Success When 1 Then 'yes' When 0 Then 'no' End As LoginSuccessful, tblLoginLog.UserName As Username, tblLoginLog.IPAddress, tblLoginLog.cert As Certificate, Case tblLoginLog.Success When 1 Then 'green' When 0 Then 'red' End As foregroundcolor, Case tblLoginLog.Success When 1 Then '#dfffdb' When 0 Then '#fbfb86' End As backgroundcolor From tblLoginLog Order By LoginDate Desc