CREATE VIEW dbo.web50repc10d43b2ef42477fadcdb8435111ff4f AS Select Top 1000000 r.AssetId As Asset, ac.Custom1 As N'Организация', ac.Custom2 As N'Город', ac.Custom3 As N'Адрес', ac.Custom4 As N'Офис', s.ScladName As N'Склад', r.*, mo.Name From rMaterialSvodRes As r Inner Join tblAssetCustom As ac On r.AssetId = ac.AssetID Left Join rAssetsScladLink As asl On r.AssetId = asl.AssetID Left Join rSclad As s On asl.IdSclad = s.Id Left Join rMaterialOriginal As mo On r.PartNumber = mo.PartNumber Order By N'Город', N'Организация', r.IdSclad, Asset