CREATE VIEW dbo.web50repc59c87315c034649933a78d4452d9368 AS Select Top 1000000 tblConfigLog.Date, Case tblConfigLog.ActionID When 1 Then 'change' When 2 Then '+' When 3 Then '-' End As Action, tblConfigLog.Description, tblConfigLog.OldValue, tblConfigLog.NewValue, tblConfigLog.displayname As ChangeMadeBy, htblusers.name, htblusers.username, htblusers.userdomain, htblusers.email, htblusers.telephone, htblusers.mobile From tblConfigLog Left Join htblusers On htblusers.userid = tblConfigLog.UserID Order By tblConfigLog.Date Desc