

CREATE VIEW [web50repchassisoverview] as SELECT TOP 1000000 tsysOS.Image As icon, tblAssets.AssetID, tblAssets.AssetName, TsysChassisTypes.ChassisName, tblSystemEnclosure.LockPresent, tblSystemEnclosure.Manufacturer AS ChassisManufacturer, tblSystemEnclosure.Version, tblSystemEnclosure.SMBIOSAssetTag, tblAssets.Domain, tblAssets.Username, tblAssets.Userdomain, tblAssets.IPAddress, tblAssets.Description, tblAssetCustom.Location, tsysIPLocations.IPLocation, tblAssets.Firstseen, tblAssets.Lastseen FROM tblAssets INNER JOIN tblAssetCustom ON tblAssets.AssetID = tblAssetCustom.AssetID INNER JOIN tsysAssetTypes ON tsysAssetTypes.AssetType = tblAssets.Assettype INNER JOIN tblSystemEnclosure ON tblAssets.AssetID = tblSystemEnclosure.AssetID INNER JOIN TsysChassisTypes ON TsysChassisTypes.Chassistype = tblSystemEnclosure.ChassisTypes Left Join tsysIPLocations ON tblAssets.LocationID = tsysIPLocations.LocationID INNER JOIN tsysOS ON tblAssets.OScode = tsysOS.OScode WHERE (tblAssetCustom.State = 1) ORDER BY tblAssets.AssetName