CREATE VIEW dbo.web50repd0ee9aa1c51147f3922ea79053d55aad AS Select Top 1000000 at.AssetTypename As N'Тип устройства', s.Statename As N'Статус', ac.Custom1 As N'Организация', ac.Custom2 As N'Город', ac.Custom3 As 'Адрес', ac.Custom4 As 'Офис', a.AssetName, ac.Model As 'Модель', ac.Serialnumber As 'Серийный №', ac.Custom7 As 'Инв. №', ac.Custom19 As 'Ответственный ВТшник', ac.Custom11 As [ФИО пользователя], ac.Comments As ['Назначение'], a.Description As ['Комментарий'], a.AssetID From tblAssets As a Inner Join tblAssetCustom As ac On a.AssetID = ac.AssetID Inner Join tsysAssetTypes As at On a.Assettype = at.AssetType Inner Join tblState As s On ac.State = s.State Where (s.Statename = 'Stock') Or (s.Statename = 'In repair') Order By N'Тип устройства', N'Организация', N'Город', N'Адрес', N'Офис'