

CREATE VIEW [web50repfileIE] AS Select Top 1000000 tsysOS.Image As icon, tblassets.AssetID, tblassets.AssetName, tblfileversions.Found, tblfileversions.FilePathfull, tblfileversions.FileVersion, tblfileversions.CompanyName, tblfileversions.Filesize, tblfileversions.Lastchanged, tblAssets.Domain, tblAssets.Username, tblAssets.Userdomain, tblAssets.IPAddress, tblAssets.Description, tblAssetCustom.Manufacturer, tblAssetCustom.Model, tblAssetCustom.Location, tsysIPLocations.IPLocation, tsysOS.OSname As OS, tblAssets.SP As SP, tblAssets.Firstseen, tblAssets.Lastseen From tblassets Inner Join tblfileversions On tblassets.AssetID = tblfileversions.AssetID Inner Join tblAssetCustom On tblAssets.AssetID = tblAssetCustom.AssetID Inner Join tsysOS On tsysOS.OScode = tblAssets.OScode Left Join tsysIPLocations ON tblAssets.LocationID = tsysIPLocations.LocationID Where tblfileversions.FilePathfull Like '%iexplore.exe' And tblassets.Assettype = -1 Order By tblassets.AssetName