

CREATE VIEW [web50replinuxpartitionlessthen10free] AS SELECT TOP 1000000 tblAssets.AssetID, tblAssets.AssetName, tblLinuxHardDisks.Filesystem, tblLinuxHardDisks.Size, tblLinuxHardDisks.Used, tblLinuxHardDisks.Percentage AS [Space used], tblLinuxHardDisks.MountedOn AS [Mounted on], tblAssets.Domain,tsysAssetTypes.AssetTypename As Type, tblAssets.IPAddress, tblAssets.Description, tblAssetCustom.Manufacturer, tblAssetCustom.Model, tblAssetCustom.Location, tsysIPLocations.IPLocation, tblAssets.Firstseen, tblAssets.Lastseen, tsysAssetTypes.AssetTypeIcon10 As icon FROM tblLinuxHardDisks INNER JOIN tblAssets ON tblLinuxHardDisks.AssetID = tblAssets.AssetID INNER JOIN tblAssetCustom ON tblAssets.AssetID = tblAssetCustom.AssetID INNER JOIN tsysAssetTypes ON tblAssets.Assettype = tsysAssetTypes.AssetType Left Join tsysIPLocations ON tblAssets.LocationID = tsysIPLocations.LocationID WHERE (REPLACE(tblLinuxHardDisks.Percentage, '%', '') > 90) AND (tblAssetCustom.State = 1) ORDER BY tblAssets.AssetName