

CREATE VIEW [web50repmacpartitionlessthen10free] AS SELECT TOP 1000000 tblAssets.AssetID, tblAssets.AssetName, tblMacPartitions.Filesystem, tblMacPartitions.Available, tblMacPartitions.Used, tblMacPartitions.Percentage AS [Space used], tblMacPartitions.MountedOn AS [Mounted on], tblAssets.Domain,tsysAssetTypes.AssetTypename As Type, tblAssets.IPAddress, tblAssets.Description, tblAssetCustom.Manufacturer, tblAssetCustom.Model, tblAssetCustom.Location, tsysIPLocations.IPLocation, tblAssets.Firstseen, tblAssets.Lastseen, tsysAssetTypes.AssetTypeIcon10 As icon FROM tblMacPartitions INNER JOIN tblAssets ON tblMacPartitions.AssetID = tblAssets.AssetID INNER JOIN tblAssetCustom ON tblAssets.AssetID = tblAssetCustom.AssetID INNER JOIN tsysAssetTypes ON tblAssets.Assettype = tsysAssetTypes.AssetType Left Join tsysIPLocations ON tblAssets.LocationID = tsysIPLocations.LocationID WHERE (tblAssetCustom.State = 1) AND (REPLACE(tblMacPartitions.Percentage, '%', '') > 10) AND (tblMacPartitions.Available <> N'0B') ORDER BY tblAssets.AssetName