

CREATE VIEW [web50repnotlatestspwin2012] AS SELECT TOP 1000000 tsysOS.Image As icon, tblAssets.AssetID, tblAssets.AssetName, tsysOS.OSname As OS, tblAssets.SP As SP, tblAssets.Domain, tblAssets.Username, tblAssets.Userdomain, tblAssets.IPAddress, tblAssets.Description, tblAssetCustom.Manufacturer, tblAssetCustom.Model, tblAssetCustom.Location, tsysIPLocations.IPLocation, tblAssets.Firstseen, tblAssets.Lastseen FROM tblAssets INNER JOIN tblAssetCustom ON tblAssets.AssetID = tblAssetCustom.AssetID INNER JOIN tsysOS ON tblAssets.OScode = tsysOS.OScode LEFT JOIN tsysIPLocations ON tblAssets.LocationID = tsysIPLocations.LocationID WHERE (tblAssets.SP <> 0) AND (tsysOS.OSname = 'Win 2012') AND (tblAssetCustom.State = 1) ORDER BY tblAssets.AssetName