
CREATE VIEW [web50reposoverview] AS SELECT TOP 1000000 tsysOS.Image As icon, tblAssets.AssetID, tblAssets.AssetName, tsysOS.OSname As OS, tblOperatingsystem.Caption, tblAssets.SP As SP, tblAssets.Domain, tblAssets.Username, tblAssets.Userdomain, tblAssets.IPAddress, tblAssets.Description, tblAssetCustom.Manufacturer, tblAssetCustom.Model, tblAssetCustom.Location, tsysIPLocations.IPLocation, tblAssets.Firstseen, tblAssets.Lastseen FROM tblAssets INNER JOIN tblOperatingsystem ON tblAssets.AssetID = tblOperatingsystem.AssetID INNER JOIN tsysOS ON tsysOS.OScode = tblAssets.OScode INNER JOIN tblAssetCustom ON tblAssets.AssetID = tblAssetCustom.AssetID LEFT JOIN tsysIPLocations ON tblAssets.LocationID = tsysIPLocations.LocationID WHERE (tblAssetCustom.State = 1) ORDER BY tsysOS.OSname, tblAssets.AssetName, tblOperatingsystem.Caption