

CREATE VIEW [web50repsqlservers] AS SELECT TOP 1000000 tsysOS.Image As icon, tblAssets.AssetID, tblAssets.AssetName, tblSqlServers.displayVersion AS Edition, tblSqlServers.skuName AS License, tblSqlServers.spLevel AS [SQL SP], tblSqlServers.serviceName AS Service, tblSqlServers.lastChanged, tblAssets.Domain, tblAssets.Username, tblAssets.Userdomain, tblAssets.IPAddress, tblAssets.Description, tblAssetCustom.Location, tsysIPLocations.IPLocation, tsysOS.OSname As OS, tblAssets.SP As SP, tblAssets.Firstseen, tblAssets.Lastseen FROM tblAssets INNER JOIN tblAssetCustom ON tblAssets.AssetID = tblAssetCustom.AssetID INNER JOIN tsysOS ON tblAssets.OScode = tsysOS.OScode INNER JOIN tblSqlServers ON tblAssets.AssetID = tblSqlServers.AssetID LEFT JOIN tsysIPLocations ON tblAssets.LocationID = tsysIPLocations.LocationID WHERE (tblAssetCustom.State = 1) ORDER BY tblAssets.AssetName, Edition