

CREATE VIEW [web50repvmwaretoolsoutofdate] AS SELECT TOP 1000000 tblAssets.AssetID, tblAssets.AssetName, tblVmwareGuest.Name, tblVmwareGuest.IsRunning, tblAssets.Domain, tsysAssetTypes.AssetTypename As Type, tblAssets.IPAddress, tblAssets.Description, tblAssetCustom.Manufacturer, tblAssetCustom.Model, tblAssetCustom.Location, tsysIPLocations.IPLocation, tblAssets.Firstseen, tblAssets.Lastseen, tsysAssetTypes.AssetTypeIcon10 As icon FROM tsysAssetTypes RIGHT OUTER JOIN tblVmwareGuest INNER JOIN tblAssets ON tblVmwareGuest.AssetID = tblAssets.AssetID INNER JOIN tblAssetCustom ON tblAssets.AssetID = tblAssetCustom.AssetID ON tsysAssetTypes.AssetType = tblAssets.Assettype Left Join tsysIPLocations ON tblAssets.LocationID = tsysIPLocations.LocationID WHERE (tblVmwareGuest.ToolsVersionStatus = 2) AND (tblAssetCustom.State = 1) ORDER BY tblAssets.AssetName